import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const ColorForm = () => {
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [hex, setHex] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic (e.g., sending data to backend or updating state)
    // For demonstration, you can update a list of colors in state (or send data to API)
    // Mock data for demonstration
    const newColor = { name, hex }; // Assuming you have form input fields for 'name' and 'hex'
    console.log('New Color:', newColor);

    // Redirect to colors index after submission
    history.push('/colors');
  };

  return (
    <div>
      <h2>Add a New Color</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="hex">Hex Value:</label>
          <input
            type="color"
            id="hex"
            value={hex}
            onChange={(e) => setHex(e.target.value)}
            required
          />
        </div>
        <button type="submit">Add Color</button>
      </form>
    </div>
  );
};

export default ColorForm;
